import React, { useState, useEffect } from 'react';
import './App.css';
import NewsCard from './components/NewsCard';

function App() {
  const [news, setNews] = useState([]);
  const [query, setQuery] = useState('');
  const [darkMode, setDarkMode] = useState(false);
  const [eyeComfortMode, setEyeComfortMode] = useState(false);

  const API_KEY = 'YOUR_GNEWS_API_KEY';

  // Fetch news articles from gnews API
  useEffect(() => {
    fetch(`https://gnews.io/api/v4/search?q=${query}&token=${API_KEY}`)
      .then(response => response.json())
      .then(data => {
        setNews(data.articles);
      })
      .catch(error => console.log(error));
  }, [query]);

  // Toggle Dark Mode
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  // Toggle Eye Comfort Mode
  const toggleEyeComfortMode = () => {
    setEyeComfortMode(!eyeComfortMode);
  };

  return (
    <div className={`App ${darkMode ? 'dark-mode' : ''} ${eyeComfortMode ? 'eye-comfort' : ''}`}>
      <header className="App-header">
        <h1>ACONEWS</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search news..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          <button onClick={() => setQuery(query)}>Search</button>
        </div>
        <div className="theme-toggle-container">
          <label className="switch">
            <input type="checkbox" checked={darkMode} onChange={toggleDarkMode} />
            <span className="slider"></span>
          </label>
          <span className="toggle-label">Dark Mode</span>

          <label className="switch">
            <input type="checkbox" checked={eyeComfortMode} onChange={toggleEyeComfortMode} />
            <span className="slider"></span>
          </label>
          <span className="toggle-label">Eye Comfort</span>
        </div>
      </header>
      <main className="news-list">
        {news.map((article, index) => (
          <NewsCard key={index} article={article} />
        ))}
      </main>
    </div>
  );
}

export default App;
