import React from 'react';
import './NewsCard.css'; // You can create this CSS file to style the card

function NewsCard({ article }) {
  const { title, description, url, image } = article;

  return (
    <div className="news-card">
      {image && <img src={image} alt={title} className="news-card-image" />}
      <div className="news-card-content">
        <h2>{title}</h2>
        <p>{description}</p>
        <a href={url} target="_blank" rel="noopener noreferrer" className="read-more">
          Read more
        </a>
      </div>
    </div>
  );
}

export default NewsCard;
